y:
* {
    font-family: consolas;
  }
#content {
    font-weight: normal;
  }

div#slide {
	background-color: #D6DAF0;
	border: 1px solid #B7C5D9;
}
header .mspfalogo {
	background-color: #D6DAF0;
	border-right-color: #D6DAF0;
}
footer .mspfalogo {
	background-color: #D6DAF0;
	border-right-color: #D6DAF0;
}
body {
	background-color: #EEF2FF;
	background-image: linear-gradient(to bottom, #D1D5EE 0px, #EEF2FF 200px);
	background-repeat: no-repeat;
	color: #000000;
}
div#container {
	background-color: #EEF2FF;
	background-image: linear-gradient(to bottom, #D1D5EE 0px, #EEF2FF 200px);
	background-repeat: no-repeat;
}
div#main {
	background-color: #EEF2FF;
}
#info > span > .spoiler.open {
	background-color: #D6DAF0;
}
.major {
	color: #e00000;
	text-shadow: 0 2px #900000;
}
a:link {
	color: #dd0000 !important;
}
a:visited {
	color: #aa0000 !important;
}
#storyicon {
	background-color: #ffffff;
}
.um {
	mix-blend-mode: darken;
}
nav {
	background-color: #EEF2FF;
	color: #000000 !important;
}
